// 색상 컬러 4종류 : milkyWay(보라빛), christmasStar(노란빛), pulsarPulse(파란빛), cherryRed(붉은빛)
const colorOptions = [
  { id: 'milkyWay', bottleColor: '#45458D', cssClass: 'milkyWay', label: 'milky way', path: milkyWayPathData, price: "25,000", description: "밤하늘의 반짝이는 은하수를 담은 듯한 잉크"},
  { id: 'pulsarPulse', bottleColor: '#006297', cssClass: 'pulsarPulse', label: 'pulsar pulse', path: pulsarPulsePathData, price: "25,000", description: "깊은 우주 속 별의 푸른 섬광을 모티브로 만든 잉크"},
  { id: 'christmasStar', bottleColor: '#d59a38', cssClass: 'christmasStar', label: 'christmas star', path: christmasStarPathData, price: "25,000", description: "크리스마스 트리 위, 황금 별의 색감을 담은 잉크"},
  { id: 'cherryRed', bottleColor: '#8b0000', cssClass: 'cherryRed', label: 'cherry red', path: cherryRedPathData, price: "25,000", description: "잘 익은 체리처럼 깊고 매혹적인 붉은 색의 잉크"},
]

function App() {
  // 배경색 변경 상태 관리
  const [selectedColor, setSelectedColor] = useState(colorOptions[0]);
  // 글씨 애니메이션 제어 관리
  const [isTextVisible, setIsTextVisible] = useState(true);
  // 수량을 위한 상태 (기본값은 1)
  const [quantity, setQuantity] = useState(1);

  const handleColorChange = (colorId: string) => {
    const newColor = colorOptions.find(c => c.id === colorId);
    if (newColor && newColor.id !== selectedColor.id) {

      setIsTextVisible(false); // 글씨 숨겨서 리셋하기
      setSelectedColor(newColor); // 새로운 색상으로 업데이트

      setTimeout(() => {
        setIsTextVisible(true);
      }, 100); // 아주 짧은 시간 뒤에 다시 보이게 해서 애니메이션 시작하기
    }
  };

  // 수량 조절 함수
  const handleIncrement = () => setQuantity(prev => prev + 1);
  const handleDecrement = () => setQuantity(prev => (prev > 1 ? prev - 1 : 1));

  // const appClassName = `App ${selectedColor.cssClass}`;

  return (
    <div className={`App ${selectedColor.cssClass}`}>
      <Handwriting
        textPathData={selectedColor.path}
        visible={isTextVisible} 
      />
      <Canvas shadows camera={{ position: [0, 5, 500], fov: 50 }}>
        {/* <color attach="background" args={isColor ? ["#2F274C"] : ["#000000"]} /> */}
        <Suspense fallback={null}>
          {selectedColor.id === 'milkyWay' && <Stars />}
          {selectedColor.id === 'christmasStar' && <Stars />}
          {selectedColor.id === 'pulsarPulse' && <Stars />}
          {selectedColor.id === 'cherryRed' && <Stars />}
        </Suspense>

        {/* <OrbitControls 
          maxDistance={700}
          minDistance={100}
          minPolarAngle={Math.PI / 4}
          maxPolarAngle={Math.PI / 2}
        /> */}

        <spotLight 
          position={[0, 1000, 100]} 
          angle={0.3} 
          penumbra={1} 
          intensity={1} 
          castShadow 
          shadow-mapSize={[1024, 1024]}
          shadow-camera-near={10}
          shadow-camera-far={5000}
          shadow-bias={-0.0001}
        />

        <Environment preset="forest" />

          <Physics gravity={[0, 0, 0]}>
            <Rig>
                <BottleWithGlass bottleColor={selectedColor.bottleColor} />
            </Rig>
          </Physics>
      </Canvas>
      <ColorPalette options={colorOptions} selectedColor={selectedColor} onColorChange={handleColorChange} />
      <div className="product-info">
        <h2 className="price">{selectedColor.price || "0"}</h2>
        <p className="description">{selectedColor.description || "제품 설명이 없습니다."}</p>

        <div className="quantity-selector">
          <button onClick={handleDecrement}>-</button>
          <span>{quantity}</span>
          <button onClick={handleIncrement}>+</button>
        </div>
        <button className="buy-button">구매하기</button>
      </div>
      <footer className="copyright">
        <p>© 2025 Ink. All rights reserved.</p>
      </footer>
    </div>
  )
}

type PaletteProps = {
  options: typeof colorOptions;
  selectedColor: typeof colorOptions[0];
  onColorChange: (id: string) => void;
}

function ColorPalette({ options, selectedColor, onColorChange }: PaletteProps) {
  return (
    <div className="palette-container">
      {options.map(option => {
        const isActive = option.id === selectedColor.id;
        const itemClassName = `palette-item ${isActive ? 'active' : ''}`;
        return (
          <div key={option.id} className={itemClassName} onClick={() => onColorChange(option.id)}>
            <div
              className="swatch"
              style={{ backgroundColor: option.bottleColor }}
            />
            <span className="label">{option.label}</span>
          </div>
        );
      })}
    </div>
  );
}

export default App

